class Solution:
    def twoSum(self, nums: List[int], target: int) -> List[int]:
        ans=[]
        temp={}
        for i in range(len(nums)):
            cal=target-nums[i] 
            if nums[i] not in temp:
                temp[cal]=i
            else:
                ans.append(i)
                a=temp[nums[i]]
                ans.append(a)

        return ans
